def keyword_check(code,keyword,keyword_list,keyword_length):
    for i in range(len(code)):
        if (i+keyword_length)<=(len(code)-1):
            word=code[i:(i+keyword_length)]
            if word in keyword_list:
                if word==keyword:
                    pass
                else:
                    part_1=code[0:i]
                    part_2=code[(i+keyword_length):len(code)]
                    new_code=part_1+keyword+part_2
                    code=new_code
            elif word not in keyword_list:
                pass
        else:
            return code
    return new_code
        
def spell_check_main(x):
    for i in range(len(x)):
        try:
            code=x[i]
            code=keyword_check(code,'if',if_list,2)
            code=keyword_check(code,'in',in_list,2)
            code=keyword_check(code,'or',or_list,2)
            code=keyword_check(code,'for',for_list,3)
            code=keyword_check(code,'try',try_list,3)
            code=keyword_check(code,'and',and_list,3)
            code=keyword_check(code,'elif',elif_list,4)
            code=keyword_check(code,'else',else_list,4)
            code=keyword_check(code,'self',self_list,4)
            code=keyword_check(code,'while',while_list,5)
            code=keyword_check(code,'except',except_list,6)
            x[i]=code
        except:
            pass
